        return(
            <>
            
            <div>This is {name}'s profile</div>
            <div key={post._id} className="card">
                <div>{post.title}</div>
                <div>{post.description}</div>
                <div>{post.price}</div>
                
            </div>

            </>

        )
    })}
},[])

}
