import {useState,useEffect} from "react";

export default function CreatePost(){
    const BASE = "https://strangers-things.herokuapp.com/api/2211-ftb-ct-web-pt"
    const[title,setTitle]=useState("")
    const[description,setDescription]=useState("")
    const[price,setPrice]=useState("")
    const[willDeliver,setWillDeliver]=useState("")



    useEffect(()=>{
        async function createPosts(){
             try{
                 const response = await fetch(`${BASE}/posts`)
                 method: "POST",
                        headers;{
                            'Content-Type'; 'application/json',
                    'Authorization'; `Bearer ${token}`
                    };
                let result = await response.json({    
                       post: {
                            title: "My favorite stuffed animal",
                            description: "This is a pooh doll from 1973. It has been carefully taken care of since I first got it.",
                            price: "$480.00",
                            willDeliver: true
                    }

             });
                 console.log(result)
                 setPosts(result.data.posts);
 
             }catch(err){
                 console.log(err)
             }
         }
         createPosts()
     },[])
    
  
     return(
            <form>
                <label for="title">Title:</label>
                <input type="text" name="title"/>
                <label for="description">Description:</label>
                <input type="text" name="description"/>
                <label for="price">Price:</label>
                <input type="text"name="price"/>
                <label for="willDeliver">Will Deliver?</label>
                <input type="checkbox"></input>
                <button type="submit">Submit</button>
            </form>
     )

     return(
        //create a login form 
        <form onSubmit={(event)=>loginUser(event)}>
            <label>Username:</label>
            <input type="text" value={userName} onChange=
            {(event)=>setUserName(event.target.value)}></input>

            <label>Password:</label>
            <input type="password" value={password} onChange=
            {(event)=>setPassword(event.target.value)}></input>
            <button type="submit">Login</button>
        </form>
    )

}
